import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Box, Button, Typography, Alert, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TablePagination, Paper, Popover, Grid, IconButton, MenuItem, Select, FormControl, InputLabel, Snackbar } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import InfoIcon from '@mui/icons-material/Info';
import axios from 'axios';
import './ChargerDetail.css';
import { useTransaction } from './TransactionContext';
import { getMappedStatus } from './ChargerOverview';
import { API_URL } from './config';

const ChargerDetail = () => {
    const { chargerId } = useParams();
    const {
        transactionId,
        setTransactionId,
        chargerDetails,
        setChargerDetails,
        recentSessions,
        setRecentSessions,
        transactionInfo,
        setTransactionInfo
    } = useTransaction();
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(5);
    const [anchorEl, setAnchorEl] = useState(null);
    const chargerDetailsIntervalRef = useRef(null);
    const transactionInfoIntervalRef = useRef(null);
    const [requestedMessage, setRequestedMessage] = useState('');
    const [availability, setAvailability] = useState('');
    const [alertMessage, setAlertMessage] = useState('');
    const [warningMessage, setWarningMessage] = useState('');
    const [alertOpen, setAlertOpen] = useState(false);
    const [loading, setLoading] = useState(true);

    const storeLatestInfo = useCallback((data) => {
        if (data) {
            setChargerDetails(data);
            setWarningMessage('');
        } else {
            setChargerDetails(prevDetails => ({
                ...prevDetails,
                charger_status: 'SuspendedEVSE'
            }));
            setWarningMessage('Connection error, the server may be down.');
        }
        setLoading(false);
    }, [setChargerDetails]);

    const fetchChargerDetails = useCallback(async () => {
        try {
            const response = await axios.get(`${API_URL}/api/chargers/${chargerId}`);
            storeLatestInfo(response.data);
        } catch (error) {
            console.error('Failed to fetch charger details:', error);
            storeLatestInfo(null);
        }
    }, [chargerId, storeLatestInfo]);

    const fetchTransactionInfo = useCallback(async () => {
        try {
            const response = await axios.get(`${API_URL}/transaction_info`, {
                params: {
                    charge_point_id: chargerId,
                    transaction_id: transactionId || ''
                }
            });
            if (response.data.transaction_id) {
                setTransactionInfo(response.data);
                setTransactionId(response.data.transaction_id);
            }
        } catch (error) {
            console.error('Failed to fetch transaction info:', error);
        }
    }, [chargerId, transactionId, setTransactionInfo, setTransactionId]);

    useEffect(() => {
        const fetchRecentSessions = async () => {
            const sessions = [];
            for (let i = 0; i < 25; i++) {
                const date = new Date();
                date.setDate(date.getDate() - i);
                const options = { month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
                const formattedDate = new Intl.DateTimeFormat('en-US', options).format(date);
                sessions.push({
                    startTime: formattedDate,
                    status: 'Completed',
                    energy: (Math.random() * 20).toFixed(2),
                    cost: `$${(Math.random() * 10).toFixed(2)}`
                });
            }
            setRecentSessions(sessions);
        };

        fetchChargerDetails();
        fetchRecentSessions();

        if (chargerDetailsIntervalRef.current) {
            clearInterval(chargerDetailsIntervalRef.current);
        }

        chargerDetailsIntervalRef.current = setInterval(fetchChargerDetails, 1000);

        return () => clearInterval(chargerDetailsIntervalRef.current);
    }, [chargerId, fetchChargerDetails, setRecentSessions]);

    useEffect(() => {
        if (chargerDetails && chargerDetails.charger_status === 'Charging') {
            fetchTransactionInfo();
            transactionInfoIntervalRef.current = setInterval(fetchTransactionInfo, 1000);
        }
        return () => {
            if (transactionInfoIntervalRef.current) {
                clearInterval(transactionInfoIntervalRef.current);
            }
        };
    }, [chargerDetails, fetchTransactionInfo]);

    const handleStartSession = async () => {
        try {
            const response = await axios.post(`${API_URL}/start_transaction`, {
                charge_point_id: chargerId,
                evse_id: parseInt(chargerDetails.evse_id || '123'),
                id_token: chargerDetails.idToken || 'TOKEN_1234',
                remote_start_id: 1
            });
            setTransactionId(response.data.transaction_id);
            setAlertMessage('Started successfully');
            setAlertOpen(true);
            setTimeout(() => {
                setAlertOpen(false);
            }, 3000);
        } catch (error) {
            console.error('Failed to start transaction:', error);
            setAlertMessage('Failed to start transaction');
            setAlertOpen(true);
            setTimeout(() => {
                setAlertOpen(false);
            }, 3000);
        }
    };

    const handleStopSession = async () => {
        try {
            await axios.post(`${API_URL}/stop_transaction`, {
                charge_point_id: chargerId,
                transaction_id: transactionId
            });
            setAlertMessage('Stopped successfully');
            setAlertOpen(true);
            setTimeout(() => {
                setAlertOpen(false);
            }, 3000);
        } catch (error) {
            console.error('Failed to stop transaction:', error);
            setAlertMessage('Failed to stop transaction');
            setAlertOpen(true);
            setTimeout(() => {
                setAlertOpen(false);
            }, 3000);
        }
    };

    const handleReset = async () => {
        try {
            await axios.post(`${API_URL}/reset`, {
                charge_point_id: chargerId
            });
            setAlertMessage('Reset successfully');
            setAlertOpen(true);
            setTimeout(() => {
                setAlertOpen(false);
            }, 3000);
        } catch (error) {
            console.error('Failed to reset charger:', error);
            setAlertMessage('Failed to reset charger');
            setAlertOpen(true);
            setTimeout(() => {
                setAlertOpen(false);
            }, 3000);
        }
    };

    const handleTriggerMessage = async () => {
        try {
            const response = await axios.post(`${API_URL}/trigger_message`, {
                charge_point_id: chargerId,
                requested_message: requestedMessage,
                evse_id: parseInt(chargerDetails.evse_id || '123'),
                connector_id: parseInt(chargerDetails.connector_id || '321')
            });
            console.log(response.data);
            setAlertMessage(`Triggered message: ${requestedMessage}`);
            setAlertOpen(true);
            setTimeout(() => {
                setAlertOpen(false);
            }, 3000);
        } catch (error) {
            console.error('Failed to trigger message:', error);
            alert('Failed to trigger message');
        }
    };

    const handleChangeAvailability = async () => {
        try {
            console.log(chargerId, availability);
            const response = await axios.post(`${API_URL}/change_availability`, {
                charge_point_id: chargerId,
                operation_status: availability,
                evse_id: parseInt(chargerDetails.evse_id || '123'),
                connector_id: parseInt(chargerDetails.connector_id || '321')
            });
            console.log(response.data);
            setAlertMessage(`Changed availability to: ${availability}`);
            setAlertOpen(true);
            setTimeout(() => {
                setAlertOpen(false);
            }, 3000);
        } catch (error) {
            console.error('Failed to change availability:', error);
            alert('Failed to change availability');
        }
    };

    const handleChargingClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleChargingClose = () => {
        setAnchorEl(null);
    };

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    const startButtonDisabled = ['Unavailable'].includes(chargerDetails.connectorStatus) || ['Charging', 'SuspendedEV', 'SuspendedEVSE'].includes(chargerDetails.charger_status);
    const stopButtonDisabled = !['Charging'].includes(chargerDetails.charger_status);
    const UnavailableDisabled = ['SuspendedEV', 'SuspendedEVSE'].includes(chargerDetails.charger_status);

    return (
        <Box className="charger-detail" display="flex" flexDirection="column" width="100%" p={2}>
            <Grid container justify="center" alignItems="center" direction="column">
                <img src={`${API_URL}/static/renesas-logo.png`} alt="RENESAS" />
                <Typography variant="h5">Charging Station Management System v2.0.1</Typography>
            </Grid>
            {warningMessage && <Alert severity="error">{warningMessage}</Alert>}
            <Box display="flex" alignItems="center" mb={2} style={{ marginBottom: '-30px' }}>
                <Link to="/" style={{ textDecoration: 'none', color: '#007bff', marginRight: '8px' }}>
                    <IconButton aria-label="back"><ArrowBackIcon /></IconButton>
                </Link>
                <Typography>/ {chargerDetails.model}</Typography>
            </Box>
            <Box display="flex" width="100%" p={2}>
                <Box flex={1} p={2}>
                    <Box display="flex" justifyContent="center" style={{ marginBottom: '-10px' }}>
                        <img src={`${API_URL}/static/charger-cable.jpg`} alt="Charger Cable" className="charger-image" style={{ width: '50%', height: 'auto' }} />
                    </Box>
                    <Table size="small" style={{ paddingTop: '0px' }}>
                        <TableBody>
                            <TableRow>
                                <TableCell><strong>Model</strong></TableCell>
                                <TableCell>{chargerDetails.model}</TableCell>
                            </TableRow>
                            <TableRow>
                                <TableCell><strong>Charger Status</strong></TableCell>
                                <TableCell>
                                    <span className={`charger_status-label ${chargerDetails.charger_status.toLowerCase()}`}>{getMappedStatus(chargerDetails.charger_status)}</span>
                                    {chargerDetails.charger_status === 'Charging' && (
                                        <IconButton aria-describedby="transaction-info-popover" color="primary" onClick={handleChargingClick} className="small_icon">
                                            <InfoIcon fontSize="small" />
                                        </IconButton>
                                    )}
                                    <Popover
                                        id="transaction-info-popover"
                                        open={Boolean(anchorEl)}
                                        anchorEl={anchorEl}
                                        onClose={handleChargingClose}
                                        anchorOrigin={{
                                            vertical: 'bottom',
                                            horizontal: 'center',
                                        }}
                                        transformOrigin={{
                                            vertical: 'top',
                                            horizontal: 'center',
                                        }}
                                    >
                                        <Paper elevation={3} sx={{ p: 2, minWidth: '150px', maxWidth: '250px' }}>
                                            {transactionInfo ? (
                                                <Grid container spacing={1}>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2"><strong>EVSE ID:</strong></Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2">{transactionInfo.evse_id}</Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2"><strong>Connector ID:</strong></Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2">{transactionInfo.connector_id}</Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2"><strong>Voltage:</strong></Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2">{transactionInfo.voltage} (V)</Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2"><strong>Current:</strong></Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2">{transactionInfo.current} (A)</Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2"><strong>Frequency:</strong></Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2">{transactionInfo.frequency} (Hz)</Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2"><strong>Power Factor:</strong></Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2">{transactionInfo.power_factor}</Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2"><strong>Active Power:</strong></Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2">{transactionInfo.active_power} (W)</Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2"><strong>Energy:</strong></Typography>
                                                    </Grid>
                                                    <Grid item xs={6}>
                                                        <Typography variant="body2">{transactionInfo.energy} (kWh)</Typography>
                                                    </Grid>
                                                </Grid>
                                            ) : (
                                                <Typography variant="body1">Loading transaction info...</Typography>
                                            )}
                                        </Paper>
                                    </Popover>
                                </TableCell>
                            </TableRow>
                            <TableRow>
                                <TableCell><strong>Connector Type</strong></TableCell>
                                <TableCell><img src={`${API_URL}/static/conn_type2.png`} alt="conn2" className="small_icon" />{chargerDetails.connectorType} ({chargerDetails.power})</TableCell>
                            </TableRow>
                            <TableRow>
                                <TableCell><strong>Connector Status</strong></TableCell>
                                <TableCell>
                                    {chargerDetails.connectorStatus}
                                </TableCell>
                            </TableRow>
                            <TableRow>
                                <TableCell><strong>Location</strong></TableCell>
                                <TableCell>{chargerDetails.location}</TableCell>
                            </TableRow>
                            <TableRow>
                                <TableCell><strong>Last Session</strong></TableCell>
                                <TableCell>{chargerDetails.lastSession.dateTime}</TableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                    <Table size="small">
                        <TableBody>
                            <TableRow>
                                <TableCell>
                                    <Button variant="contained" color="primary" onClick={handleStartSession} disabled={startButtonDisabled} fullWidth>Start</Button>
                                </TableCell>
                                <TableCell>
                                    <Button variant="contained" color="secondary" onClick={handleStopSession} disabled={stopButtonDisabled} fullWidth>Stop</Button>
                                </TableCell>
                                <TableCell>
                                    <Button variant="contained" color='warning' onClick={handleReset} disabled={UnavailableDisabled} fullWidth>Reset</Button>
                                </TableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                    <Table size="small">
                        <TableBody>
                            <TableRow>
                                <TableCell style={{ width: '30%' }}>
                                    <Button 
                                        variant="contained" 
                                        color="inherit"
                                        onClick={handleTriggerMessage}
                                        disabled={requestedMessage === ""}
                                        fullWidth>
                                        Trigger
                                    </Button>
                                </TableCell>
                                <TableCell style={{ width: '58%' }}>
                                    <FormControl fullWidth>
                                        <InputLabel id="trigger-message-label">Trigger message type</InputLabel>
                                        <Select
                                            labelId="trigger-message-label"
                                            value={requestedMessage}
                                            onChange={(e) => setRequestedMessage(e.target.value)}
                                            disabled={UnavailableDisabled}
                                            displayEmpty
                                        >
                                            <MenuItem value="BootNotification">BootNotification</MenuItem>
                                            <MenuItem value="FirmwareStatusNotification">FirmwareStatusNotification</MenuItem>
                                            <MenuItem value="Heartbeat">Heartbeat</MenuItem>
                                            <MenuItem value="StatusNotification">StatusNotification</MenuItem>
                                            <MenuItem value="TransactionEvent">TransactionEvent</MenuItem>
                                            <MenuItem value="">{'\u00A0'}</MenuItem>
                                        </Select>
                                    </FormControl>
                                </TableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                    <Table size="small">
                        <TableBody>
                            <TableRow>
                                <TableCell style={{ width: '30%' }}>
                                    <Button
                                        variant="contained"
                                        color="inherit"
                                        onClick={handleChangeAvailability}
                                        disabled={availability === ""}
                                        fullWidth>
                                        Change
                                    </Button>
                                </TableCell>
                                <TableCell style={{ width: '58%' }}>
                                    <FormControl fullWidth>
                                    <InputLabel id="availability-label">Availability type</InputLabel>
                                        <Select
                                            labelId="availability-label"
                                            value={availability}
                                            onChange={(e) => setAvailability(e.target.value)}
                                            disabled={UnavailableDisabled}
                                            displayEmpty
                                        >
                                            <MenuItem value="Operative">Operative</MenuItem>
                                            <MenuItem value="Inoperative">Inoperative</MenuItem>
                                            <MenuItem value="">{'\u00A0'}</MenuItem>
                                        </Select>
                                    </FormControl>
                                </TableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                </Box>
                <Box flex={1} p={2}>
                    <Box display="flex" justifyContent="center">
                        <Typography variant="h5">Recent Sessions</Typography>
                    </Box>
                    <TableContainer component={Paper}>
                        <Table size="small">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Start Time</TableCell>
                                    <TableCell>Status</TableCell>
                                    <TableCell>Energy Used (kWh)</TableCell>
                                    <TableCell>Cost</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {recentSessions.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((session, index) => (
                                    <TableRow key={index}>
                                        <TableCell>
                                            {session.startTime && !isNaN(new Date(session.startTime).getTime()) ? new Intl.DateTimeFormat('en-US', { month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' }).format(new Date(session.startTime)) : "Invalid date"}
                                        </TableCell>
                                        <TableCell>{session.status}</TableCell>
                                        <TableCell>{session.energy}</TableCell>
                                        <TableCell>{session.cost}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                        <TablePagination
                            rowsPerPageOptions={[5, 7, 10]}
                            component="div"
                            count={recentSessions.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={handleChangePage}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                            style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}
                        />
                    </TableContainer>
                </Box>
            </Box>
            <Snackbar
                anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
                open={alertOpen}
                message={alertMessage}
                onClose={() => setAlertOpen(false)}
                autoHideDuration={3000}
            />
        </Box>
    );
};

export default ChargerDetail;
