import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ChargerOverview.css';
import { Box, Button, Typography, Alert, Select, MenuItem, FormControl, InputLabel, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Checkbox, Modal, TextField, Fade, Backdrop } from '@mui/material';
import Grid from '@mui/material/Grid';
import { API_URL } from './config';

export function getMappedStatus(charger_status) {
    const statusMapping = {
        "Idle": "Available",
        "Charging": "Charging",
        "EVConnected": "EV Connected",
        "SuspendedEVSE": "Suspended",
        "SuspendedEV": "Suspended",
    };
    return statusMapping[charger_status] || charger_status;
}

const ChargerOverview = () => {
    const [chargers, setChargers] = useState([]);
    const [locations, setLocations] = useState([]);
    const [charging_state, setChargingStatuses] = useState([]);
    const [selectedChargers, setSelectedChargers] = useState([]);
    const [showEditModal, setShowEditModal] = useState(false);
    const [showAddModal, setShowAddModal] = useState(false);
    const [editCharger, setEditCharger] = useState(null);
    const [filters, setFilters] = useState({ location: '', status: '' });
    const navigate = useNavigate();
    const [warningMessage, setWarningMessage] = useState('');
    const [loading, setLoading] = useState(true);

    const storeLatestInfo = useCallback((data) => {
        if (Array.isArray(data)) {
            setChargers(data);
            setWarningMessage('');
        } else {
            setChargers(prevDetails => prevDetails.map(charger => ({
                ...charger,
                charger_status: 'SuspendedEVSE'
            })));
            setWarningMessage('Connection error, the server may be down.');
        }
        setLoading(false);
    }, [setChargers, setWarningMessage]);

    const fetchChargerStatuses = useCallback(async () => {
        try {
            const response = await axios.get(`${API_URL}/api/chargers`);
            storeLatestInfo(response.data);
        } catch (error) {
            console.error('Error fetching charger statuses:', error);
            storeLatestInfo(null);
        }
    }, [storeLatestInfo]);

    useEffect(() => {
        const fetchLocations = async () => {
            try {
                const response = await axios.get(`${API_URL}/api/locations`);
                setLocations(response.data);
            } catch(error) {
                console.error('Error fetching locations:', error);
                storeLatestInfo(null);
            }
        };

        const fetchChargingStatuses = async () => {
            try {
                const response = await axios.get(`${API_URL}/api/charging_state`);
                setChargingStatuses(response.data);
            } catch(error) {
                console.error('Error fetching charging states:', error);
                storeLatestInfo(null);
            }
        };

        fetchLocations();
        fetchChargingStatuses();

        const interval = setInterval(fetchChargerStatuses, 5000);
        return () => clearInterval(interval);
    }, [fetchChargerStatuses, storeLatestInfo]);

    useEffect(() => {
        fetchChargerStatuses();
    }, [fetchChargerStatuses]);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters({ ...filters, [name]: value });
    };

    const handleCheckboxChange = (e, id) => {
        if (e.target.checked) {
            setSelectedChargers([...selectedChargers, id]);
        } else {
            setSelectedChargers(selectedChargers.filter(chargerId => chargerId !== id));
        }
    };

    const handleEditSelected = () => {
        const chargerToEdit = chargers.find(charger => charger.id === selectedChargers[0]);
        setEditCharger(chargerToEdit);
        setShowEditModal(true);
    };

    const handleAddCharger = () => {
        setEditCharger(null);
        setShowAddModal(true);
    };

    const handleSaveCharger = async () => {
        if (editCharger) {
            // Edit existing charger
            await axios.put(`${API_URL}/api/chargers/${editCharger.id}`, editCharger);
            setChargers(chargers.map(charger => (charger.id === editCharger.id ? editCharger : charger)));
        } else {
            // Add new charger
            const newCharger = {
                id: String(chargers.length + 1), // Generate a new id
                ...editCharger
            };
            await axios.post(`${API_URL}/api/chargers`, newCharger);
            setChargers([...chargers, newCharger]);
        }
        setShowEditModal(false);
        setShowAddModal(false);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditCharger({ ...editCharger, [name]: value });
    };

    const handleChargerClick = (id) => {
        navigate(`/charger/${id}`);
    };

    const filteredChargers = Array.isArray(chargers) ? chargers.filter(charger =>
        (filters.location === '' || charger.location === filters.location) &&
        (filters.status === '' || charger.charger_status === filters.status)
    ) : [];

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="charger-overview">
            <Grid container justify="center" alignItems="center" direction="column">
                <img src={`${API_URL}/static/renesas-logo.png`} alt="RENESAS" />
                <Typography variant="h5">Charging Station Management System v2.0.1</Typography>
            </Grid>
            {warningMessage && <Alert severity="error">{warningMessage}</Alert>}
            <Box display="flex" gap={2} mb={2}>
                <FormControl fullWidth>
                    <InputLabel id="location-filter-label" shrink={filters.location !== ''}>Location</InputLabel>
                    <Select
                        labelId="location-filter-label"
                        name="location"
                        value={filters.location}
                        onChange={handleFilterChange}
                    >
                        <MenuItem value="">All Locations</MenuItem>
                        {locations.map(location => (
                            <MenuItem key={location} value={location}>{location}</MenuItem>
                        ))}
                    </Select>
                </FormControl>
                <FormControl fullWidth>
                    <InputLabel id="status-filter-label" shrink={filters.status !== ''}>Status</InputLabel>
                    <Select
                        labelId="status-filter-label"
                        name="status"
                        value={filters.status}
                        onChange={handleFilterChange}
                    >
                        <MenuItem value="">All Statuses</MenuItem>
                        {charging_state.map(status => (
                            <MenuItem key={status} value={status}>{status}</MenuItem>
                        ))}
                    </Select>
                </FormControl>
            </Box>
            <Box display="flex" gap={1} mb={2} alignItems="flex-end">
                <Button
                    variant="outlined"
                    color="primary"
                    onClick={handleEditSelected} 
                    disabled={!selectedChargers.length}
                    style={{
                        backgroundColor: selectedChargers.length ? 'transparent' : 'none',
                        pointerEvents: selectedChargers.length ? 'auto' : 'none'
                    }}
                >
                    Edit Selected Chargers
                </Button>
                <Button
                    variant="outlined"
                    color="primary"
                    onClick={handleAddCharger}
                    style={{
                        backgroundColor: 'transparent',
                    }}
                >
                    Add Chargers
                </Button>
            </Box>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow className="table-header">
                            <TableCell></TableCell>
                            <TableCell><strong>Charger</strong></TableCell>
                            <TableCell><strong>Location</strong></TableCell>
                            <TableCell><strong>Status</strong></TableCell>
                            <TableCell><strong>Note</strong></TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {filteredChargers.map((charger) => (
                            <TableRow key={charger.id}>
                                <TableCell>
                                    <Checkbox 
                                        onChange={(e) => handleCheckboxChange(e, charger.id)} 
                                    />
                                </TableCell>
                                <TableCell>
                                    <Button 
                                        onClick={() => handleChargerClick(charger.id)} 
                                        style={{ textTransform: 'none' }}
                                    >
                                        {charger.model}
                                    </Button>
                                </TableCell>
                                <TableCell>{charger.location}</TableCell>
                                <TableCell>
                                    <span className={`charger_status-label ${charger.charger_status.toLowerCase()}`}>{getMappedStatus(charger.charger_status)}</span>
                                </TableCell>
                                <TableCell>{charger.note}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <Modal
                open={showEditModal}
                onClose={() => setShowEditModal(false)}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={showEditModal}>
                    <Box sx={{ p: 4, bgcolor: 'background.paper', borderRadius: 1, width: '400px', mx: 'auto', mt: '10%' }}>
                        <Typography variant="h6" gutterBottom>Edit Charger</Typography>
                        <FormControl fullWidth>
                            <TextField
                                label="Model"
                                name="model"
                                value={editCharger?.model || ''}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                            />
                            <TextField
                                label="Location"
                                name="location"
                                value={editCharger?.location || ''}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                            />
                            <TextField
                                label="Status"
                                name="status"
                                value={editCharger?.status || ''}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                            />
                            <TextField
                                label="Note"
                                name="note"
                                value={editCharger?.note || ''}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                            />
                            <Button onClick={handleSaveCharger} variant="contained" color="primary" fullWidth>
                                Save Changes
                            </Button>
                        </FormControl>
                    </Box>
                </Fade>
            </Modal>
            <Modal
                open={showAddModal}
                onClose={() => setShowAddModal(false)}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={showAddModal}>
                    <Box sx={{ p: 4, bgcolor: 'background.paper', borderRadius: 1, width: '400px', mx: 'auto', mt: '10%' }}>
                        <Typography variant="h6" gutterBottom>Add Charger</Typography>
                        <FormControl fullWidth>
                            <TextField
                                label="Model"
                                name="model"
                                value={editCharger?.model || ''}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                            />
                            <TextField
                                label="Location"
                                name="location"
                                value={editCharger?.location || ''}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                            />
                            <TextField
                                label="Status"
                                name="status"
                                value={editCharger?.status || ''}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                            />
                            <TextField
                                label="Last Used"
                                name="lastUsed"
                                value={editCharger?.lastUsed || ''}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                            />
                            <Button onClick={handleSaveCharger} variant="contained" color="primary" fullWidth>
                                Save Changes
                            </Button>
                        </FormControl>
                    </Box>
                </Fade>
            </Modal>
        </div>
    );
};

export default ChargerOverview;
